        const res = JSON.parse(response);
        {res && setAuth(res)}
            console.log(res)
        },[]);

    const [auth, setAuth] = useState({
        // authenticated: true,
        // name: "brahim",
        // prenom:"elaboudi",prof:true

    })
    const logout = () => {
        setAuth({})
        localStorage.removeItem()
    }
    return (
        <AuthContext.Provider value={{ auth, setAuth, logout }}>
            {props.children}
        </AuthContext.Provider>
    )
}




