import React, { useContext, useState } from 'react';
import '../component css/login.css'
import { Link } from "react-router-dom";
import { AuthContext } from '../AuthContex';


function Login() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [pass, setPass] = useState(true);
    //pour utilier authcontext du store
    const  authcontex=useContext(AuthContext);

    const handleSubmit = (e) => {
        e.preventDefault();

        const response=loginUser({email,pass})

        alert(`Bienvenue ${email} !`);
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <h1 className="login-title">Connexion</h1>
                <form className="login-form" onSubmit={handleSubmit}>
                    <div className="login-input-group">
                        <label className="login-label" htmlFor="email">
                            Adresse email
                        </label>
                        <input
                            className="login-input"
                            type="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                    <div className="login-input-group">
                        <label className="login-label" htmlFor="password">
                            Mot de passe
                        </label>
                        <input
                            className="login-input"
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    {!authcontex.pass && <p className='msg-error'>Désolé, votre email ou  mot de passe est incorrect.
                         Veuillez vérifier votre mot de passe.</p>}
                    <button className="login-button" type="submit">
                        Se connecter
                    </button>
                </form>
                <div className="login-links">
                    <Link to="/" className="login-link">Accueil</Link>
                    <Link to="/register" className="login-link">Inscription</Link>
                </div>
            </div>
        </div>
    );
}



export default Login
